import React from 'react';
import { useLazyImage } from '../../hooks/useLazyImage';

interface LazyImageProps {
  src: string;
  alt: string;
  className?: string;
  placeholder?: string;
  fallback?: string;
  onLoad?: () => void;
  onError?: () => void;
}

const LazyImage: React.FC<LazyImageProps> = ({
  src,
  alt,
  className = '',
  placeholder = 'data:image/svg+xml;base64,',
  fallback = 'data:image/svg+xml;base64,',
  onLoad,
  onError,
}) => {
  const { imgRef, isLoaded, error, src: imageSrc } = useLazyImage({ src, alt });

  const handleLoad = () => {
    onLoad?.();
  };

  const handleError = () => {
    onError?.();
  };

  const displaySrc = error ? fallback : (imageSrc || placeholder);

  return (
    <img
      ref={imgRef}
      src={displaySrc}
      alt={alt}
      className={`transition-opacity duration-300 ${isLoaded ? 'opacity-100' : 'opacity-50'} ${className}`}
      onLoad={handleLoad}
      onError={handleError}
    />
  );
};

export default LazyImage; 